<?php if (!defined("XOOPS_ROOT_PATH")) {    die("XOOPS root path not defined");}class audio_broken extends XoopsObject{// constructor	function __construct()	{		$this->XoopsObject();        $this->initVar("reportid",XOBJ_DTYPE_INT,null,false,5);		$this->initVar("lid",XOBJ_DTYPE_INT,null,false,11);        $this->initVar("sender",XOBJ_DTYPE_INT,null,false,11);		$this->initVar("ip",XOBJ_DTYPE_TXTBOX,null,false);        //pour les jointures:        $this->initVar("title",XOBJ_DTYPE_TXTBOX, null, false);        $this->initVar("cid",XOBJ_DTYPE_INT,null,false,5);    }    function audio_broken()    {        $this->__construct();    }
    function getForm($lid, $action = false)    {		global $xoopsDB, $xoopsModule, $xoopsModuleConfig;		if ($action === false) {			$action = $_SERVER['REQUEST_URI'];		}                $form = new XoopsThemeForm(_MD_AUDIO_BROKENFILE_REPORTBROKEN, 'brokenform', 'brokenfile.php', 'post');        $form->setExtra('enctype="multipart/form-data"');        $form->addElement(new XoopsFormCaptcha(), true);                $form->addElement(new XoopsFormHidden('op', 'save'));        $form->addElement(new XoopsFormHidden('lid', $lid));        // Submit button		        $button_tray = new XoopsFormElementTray(_MD_AUDIO_BROKENFILE_REPORTBROKEN, '' ,'');        $button_tray->addElement(new XoopsFormButton('', 'post', _MD_AUDIO_BROKENFILE_REPORTBROKEN, 'submit'));        $form->addElement($button_tray);    	return $form;    }}
class audioaudio_brokenHandler extends XoopsPersistableObjectHandler {	function __construct(&$db) 	{		parent::__construct($db, "audio_broken", 'audio_broken', 'reportid', 'lid');    }}?>