<?php
if (!defined("XOOPS_ROOT_PATH")) {    die("XOOPS root path not defined");}class audio_mod extends XoopsObject{// constructor	function __construct()	{		$this->XoopsObject();		$this->initVar("requestid",XOBJ_DTYPE_INT,null,false,11);        $this->initVar("lid",XOBJ_DTYPE_INT,null,false,11);		$this->initVar("cid",XOBJ_DTYPE_INT,null,false,5);		$this->initVar("title",XOBJ_DTYPE_TXTBOX, null, false);		$this->initVar("url",XOBJ_DTYPE_TXTBOX, null, false);		$this->initVar("homepage",XOBJ_DTYPE_TXTBOX, null, false);		$this->initVar("version",XOBJ_DTYPE_TXTBOX, null, false);        $this->initVar("size",XOBJ_DTYPE_TXTBOX, null, false);        $this->initVar("platform",XOBJ_DTYPE_TXTBOX, null, false);        $this->initVar("logourl",XOBJ_DTYPE_TXTBOX, null, false);        $this->initVar("description",XOBJ_DTYPE_TXTAREA, null, false);		// Pour autoriser le html		$this->initVar('dohtml', XOBJ_DTYPE_INT, 1, false);        		$this->initVar("modifysubmitter",XOBJ_DTYPE_INT,null,false,11);	}
	function audio_mod($lid)    {        $this->__construct();            }
    function get_new_enreg()    {        global $xoopsDB;        $new_enreg = $xoopsDB->getInsertId();        return $new_enreg;    }
    function getForm($lid, $erreur, $donnee = array(), $action = false)    {                global $xoopsDB, $xoopsModule, $xoopsModuleConfig, $xoopsUser;		if ($action === false) {			$action = $_SERVER['REQUEST_URI'];		}        $groups = is_object($xoopsUser) ? $xoopsUser->getGroups() : XOOPS_GROUP_ANONYMOUS;        $gperm_handler =& xoops_gethandler('groupperm');        $perm_upload = ($gperm_handler->checkRight('audio_ac', 32, $groups, $xoopsModule->getVar('mid'))) ? true : false ;        //appel des class        $downloads_Handler =& xoops_getModuleHandler('audio_downloads', 'audio');        $downloadscat_Handler =& xoops_getModuleHandler('audio_cat', 'audio');                $view_downloads = $downloads_Handler->get($lid);       include_once(XOOPS_ROOT_PATH."/class/xoopsformloader.php");       // affectation des variables        if ($erreur == true) {        	$d_title = $donnee['title'];            $d_cid = $donnee['cid'];            $d_homepage = $donnee['homepage'];            $d_version = $donnee['version'];            $d_size = $donnee['size'];            $d_platform = $donnee['platform'];            $d_description = $donnee['description'];                    }else{            $d_title = $view_downloads->getVar('title');            $d_cid = $view_downloads->getVar('cid');            $d_homepage = $view_downloads->getVar('homepage');            $d_version = $view_downloads->getVar('version');            $d_size = $view_downloads->getVar('size');            $d_platform = $view_downloads->getVar('platform');            $d_description = $view_downloads->getVar('description', 'e');        }        //nom du formulaire        $title = sprintf(_AM_AUDIO_FORMEDIT);         //cr�ation du formulaire        $form = new XoopsThemeForm($title, 'form', $action, 'post', true);        $form->setExtra('enctype="multipart/form-data"');        //titre        $form->addElement(new XoopsFormText(_AM_AUDIO_FORMTITLE, 'title', 50, 255, $d_title), true);        //cat�gorie        $downloadscat_Handler =& xoops_getModuleHandler('audio_cat', 'audio');        $categories = audio_MygetItemIds('audio_submit', 'audio');        $criteria = new CriteriaCompo();        $criteria->setSort('cat_weight ASC, cat_title');        $criteria->setOrder('ASC');        if ($xoopsUser) {            if ( !$xoopsUser->isAdmin($xoopsModule->mid()) ) {                 $criteria->add(new Criteria('cat_cid', '(' . implode(',', $categories) . ')','IN'));            }        }else{            $criteria->add(new Criteria('cat_cid', '(' . implode(',', $categories) . ')','IN'));        }        $downloadscat_arr = $downloadscat_Handler->getall($criteria);        if (count($downloadscat_arr) == 0){            redirect_header('index.php', 2,  _NOPERM);        }				$mytree = new XoopsObjectTree($downloadscat_arr, 'cat_cid', 'cat_pid');		$form->addElement(new XoopsFormLabel(_AM_AUDIO_FORMINCAT, $mytree->makeSelBox('cid', 'cat_title','--', $d_cid,true)), true);                       //affichage des champs                $downloadsfield_Handler =& xoops_getModuleHandler('audio_field', 'audio');        $criteria = new CriteriaCompo();        $criteria->setSort('weight ASC, title');        $criteria->setOrder('ASC');        $downloads_field = $downloadsfield_Handler->getall($criteria);        foreach (array_keys($downloads_field) as $i) {            if ($downloads_field[$i]->getVar('status_def') == 1){                if ($downloads_field[$i]->getVar('fid') == 1){                    //page d'accueil                    if ($downloads_field[$i]->getVar('status') == 1){                        $form->addElement(new XoopsFormText(_AM_AUDIO_FORMHOMEPAGE, 'homepage', 50, 255, $d_homepage));                    }else{                        $form->addElement(new XoopsFormHidden('homepage', ''));                    }                }                if ($downloads_field[$i]->getVar('fid') == 2){                    //version                    if ($downloads_field[$i]->getVar('status') == 1){                        $form->addElement(new XoopsFormText(_AM_AUDIO_FORMVERSION, 'version', 10, 255, $d_version));                    }else{                        $form->addElement(new XoopsFormHidden('version', ''));                    }                }                if ($downloads_field[$i]->getVar('fid') == 4){                    //plateforme                    if ($downloads_field[$i]->getVar('status') == 1){                        $platformselect = new XoopsFormSelect(_AM_AUDIO_FORMPLATFORM, 'platform', explode('|',$d_platform), 5, true);                        $platform_array = explode('|',$xoopsModuleConfig['plateform']);                        foreach( $platform_array as $platform ) {                            $platformselect->addOption("$platform", $platform);                        }                        $form->addElement($platformselect, false);                    }else{                        $form->addElement(new XoopsFormHidden('platform', ''));                    }                }            }else{                $contenu = '';                $nom_champ = 'champ' . $downloads_field[$i]->getVar('fid');                $downloadsfielddata_Handler =& xoops_getModuleHandler('audio_fielddata', 'audio');                $criteria = new CriteriaCompo();                $criteria->add(new Criteria('lid', $view_downloads->getVar('lid')));                $criteria->add(new Criteria('fid', $downloads_field[$i]->getVar('fid')));                $downloadsfielddata = $downloadsfielddata_Handler->getall($criteria);                foreach (array_keys($downloadsfielddata) as $j) {                    if ($erreur == true) {                        $contenu = $donnee[$nom_champ];                    }else{                        $contenu = $downloadsfielddata[$j]->getVar('data');                                            }                }                if ($downloads_field[$i]->getVar('status') == 1){                    $form->addElement(new XoopsFormText($downloads_field[$i]->getVar('title'), $nom_champ, 50, 255, $contenu));                }else{                    $form->addElement(new XoopsFormHidden($nom_champ, ''));                }            }                    }                //description        $editor_configs=array();    	$editor_configs["name"] ="description";    	$editor_configs["value"] = $d_description;    	$editor_configs["rows"] = 20;    	$editor_configs["cols"] = 60;    	$editor_configs["width"] = "100%";    	$editor_configs["height"] = "400px";    	$editor_configs["editor"] = $xoopsModuleConfig['editor'];        $form->addElement( new XoopsFormEditor(_AM_AUDIO_FORMTEXTDOWNLOADS, "description", $editor_configs), true);        //image        if ( $xoopsModuleConfig['useshots']){            $downloadscat_img = $view_downloads->getVar('logourl') ? $view_downloads->getVar('logourl') : 'blank.gif';            $uploadirectory='/uploads/audio/images/shots';            $imgtray = new XoopsFormElementTray(_AM_AUDIO_FORMIMG,'<br />');            $imgpath=sprintf(_AM_AUDIO_FORMPATH, $uploadirectory );            $imageselect= new XoopsFormSelect($imgpath, 'logo_img',$downloadscat_img);                    $topics_array = XoopsLists :: getImgListAsArray( XOOPS_ROOT_PATH . $uploadirectory );            foreach( $topics_array as $image ) {                $imageselect->addOption("$image", $image);            }            $imageselect->setExtra( "onchange='showImgSelected(\"image3\", \"logo_img\", \"" . $uploadirectory . "\", \"\", \"" . XOOPS_URL . "\")'" );            $imgtray->addElement($imageselect,false);            $imgtray -> addElement( new XoopsFormLabel( '', "<br /><img src='" . $view_downloads->getVar('url') . "/" . $uploadirectory . "/" . $downloadscat_img . "' name='image3' id='image3' alt='' />" ) );            $fileseltray= new XoopsFormElementTray('','<br />');            if ($perm_upload == true) {                $fileseltray->addElement(new XoopsFormFile(_AM_AUDIO_FORMUPLOAD , 'attachedimage', $xoopsModuleConfig['maxuploadsize']), false);            }            $imgtray->addElement($fileseltray);            $form->addElement($imgtray);        }        $form->addElement(new XoopsFormCaptcha(), true);        $form->addElement(new XoopsFormHidden('lid', $lid));        //pour enregistrer le formulaire        $form->addElement(new XoopsFormHidden('op', 'save'));        //bouton d'envoi du formulaire        $form->addElement(new XoopsFormButton('', 'submit', _SUBMIT, 'submit'));    	return $form;    }}
class audioaudio_modHandler extends XoopsPersistableObjectHandler {	function __construct(&$db) 	{		parent::__construct($db, "audio_mod", 'audio_mod', 'requestid', 'lid');    }}?>