<?phpclass _3gp{    public $ffmpegPath;    public $srcPath;    public $destPath;    public $input;    public $output;    public $outputWidth;    public $outputHeight;    public $audioSampleRate;    public $audioBitRate;    /**     * @return the $ffmpegPath     */    public function getFfmpegPath()    {        return $this->ffmpegPath;    }    /**     * @param field_type $ffmpegPath     */    public function setFfmpegPath($ffmpegPath)    {        $this->ffmpegPath = $ffmpegPath;    }    /**     * @return the $srcPath     */    public function getSrcPath()    {        return $this->srcPath;    }    /**     * @param field_type $srcPath     */    public function setSrcPath($srcPath)    {        $this->srcPath = $srcPath;    }    /**     * @return the $destPath     */    public function getDestPath()    {        return $this->destPath;    }    /**     * @param field_type $destPath     */    public function setDestPath($destPath)    {        $this->destPath = $destPath;    }    /**     * @return the $input     */    public function getInput()    {        return $this->input;    }    /**     * @param field_type $input     */    public function setInput($input)    {        $this->input = $input;    }    /**     * @return the $output     */    public function getOutput()    {        return $this->output;    }    /**     * @param field_type $output     */    public function setOutput($output)    {        $this->output = $output;    }    /**     * @return the $outputWidth     */    public function getOutputWidth()    {        return $this->outputWidth;    }    /**     * @param field_type $outputWidth     */    public function setOutputWidth($outputWidth)    {        $this->outputWidth = $outputWidth;    }    /**     * @return the $outputHeight     */    public function getOutputHeight()    {        return $this->outputHeight;    }    /**     * @param field_type $outputHeight     */    public function setOutputHeight($outputHeight)    {        $this->outputHeight = $outputHeight;    }    /**     * @return the $audioSampleRate     */    public function getAudioSampleRate()    {        return $this->audioSampleRate;    }    /**     * @param field_type $audioSampleRate     */    public function setAudioSampleRate($audioSampleRate)    {        $this->audioSampleRate = $audioSampleRate;    }    /**     * @return the $audioBitRate     */    public function getAudioBitRate()    {        return $this->audioBitRate;    }    /**     * @param field_type $audioBitRate     */    public function setAudioBitRate($audioBitRate)    {        $this->audioBitRate = $audioBitRate;    }    public function _3gp($ffmpeg, $srcPath, $destPath, $outWidth = 176, $outHeight = 144, $sampleRate = 800, $bitRate = "10.2k")    {        $this->setFfmpegPath($ffmpeg);        $this->setSrcPath($srcPath);        $this->setDestPath($destPath);        $this->setOutputWidth($outWidth);        $this->setOutputHeight($outHeight);        $this->setAudioSampleRate($sampleRate);        $this->setAudioBitRate($bitRate);    }    public function convert($input, $output)    {        $this->setInput($input);        $this->setOutput($output);        $command = $this->ffmpegPath . " -i " . $this->srcPath . "\\" . $this->input . " -ar " . $this->audioSampleRate . " -ab " . $this->audioBitRate . " -s " . $this->outputWidth . "x" . $this->outputHeight . " -b 120000 -vcodec h263  -ac 1  " . $this->destPath . "\\" . $this->output;        exec($command);        return $command;    }}