<?phpif (!defined("XOOPS_ROOT_PATH")) {	die("XOOPS root path not defined");}class video_fielddata extends XoopsObject{	// constructor	function __construct()	{		$this->XoopsObject();		$this->initVar("iddata",XOBJ_DTYPE_INT,null,false,11);		$this->initVar("fid",XOBJ_DTYPE_INT,null,false,11);		$this->initVar("lid",XOBJ_DTYPE_INT,null,false,11);		$this->initVar("data",XOBJ_DTYPE_TXTBOX, null, false);		$this->initVar('dohtml', XOBJ_DTYPE_INT, 1, false);	}
	function video_fielddata()	{		$this->__construct();	}}
class videovideo_fielddataHandler extends XoopsPersistableObjectHandler{	function __construct(&$db)	{		parent::__construct($db, "video_fielddata", 'video_fielddata', 'iddata', 'data');	}}?>