<?phpfunction xoops_module_update_video() {	// Update DB	$db =& Database::getInstance();	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `cid` `cat_cid` INT( 5 ) UNSIGNED NOT NULL AUTO_INCREMENT ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `pid` `cat_pid` INT( 5 ) UNSIGNED NOT NULL DEFAULT '0' ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `title` `cat_title` VARCHAR( 255 ) NOT NULL ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `imgurl` `cat_imgurl` VARCHAR( 255 ) NOT NULL ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `description_main` `cat_description_main` TEXT NOT NULL ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` CHANGE `weight` `cat_weight` INT( 11 ) NOT NULL DEFAULT '0' ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` ADD `paypal` VARCHAR( 255 ) NOT NULL;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` CHANGE `size` `size` VARCHAR( 15 ) NOT NULL DEFAULT '';";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_mod') . "` CHANGE `size` `size` VARCHAR( 15 ) NOT NULL DEFAULT '';";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` ADD `duration` VARCHAR( 15 ) NOT NULL DEFAULT '';";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` CHANGE `top` `top` TINYINT( 1 ) NOT NULL DEFAULT '0' ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_cat') . "` ADD `cat_tab` TINYINT( 1 ) NOT NULL DEFAULT '0' ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` ADD `extra` TEXT NOT NULL;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` ADD `related` VARCHAR( 255 ) NOT NULL DEFAULT '' ;";	$db->query($sql);	$sql = "ALTER TABLE `" . $db->prefix('video_downloads') . "` ADD type enum('flv','mp4') NOT NULL default 'flv';";	$db->query($sql);	// Build DIR	$dir = XOOPS_ROOT_PATH."/uploads/video/mp4";	if(!is_dir($dir))	mkdir($dir, 0777);	chmod($dir, 0777);	$indexFile = XOOPS_ROOT_PATH."/modules/video/include/index.html";	copy($indexFile, XOOPS_ROOT_PATH."/uploads/video/mp4/index.html");	return true;}?>